mapper.insert:
INSERT INTO
		NOTICE (
				NOTICE_NO 
				, member_email
				, NOTICE_TITLE
				, NOTICE_CONTENT 
				, NOTICE_DATE
				, NOTICE_FILES
				, NOTICE_PUB
		)
		VALUES (
				NOTICE_SEQ.NEXTVAL
				, 'admin@eutcha.com'
				, #{noticeTitle}
				, #{noticeContent}
				, now()
				, #{noticeFiles}
				, #{noticePub}
				)

mapper.update:
UPDATE NOTICE
	SET 
		NOTICE_TITLE = #{noticeTitle}
		, NOTICE_CONTENT = #{noticeContent}
		, NOTICE_DATE = #{noticeDate}
		, NOTICE_FILES = #{noticeFiles}
		, NOTICE_PUB = #{noticePub}
	WHERE 
		NOTICE_NO = #{noticeIdx}
